import React from "react";
import "./Message.css";
function Message({ user, message, classs }) {
  if (user) {
    return (
      <div className={`messageBox ${classs}`}>{`${user}: ${message}`}</div>
    );
  } else {
    return (
      <div className="messageBox right">
        <h1>{message}</h1>
      </div>
    );
  }
}

export default Message;
